class Person extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clickCount: 0
        }
    }
    handleClick = () => {
        this.setState({
            clickCount: this.state.clickCount + 1
        })
    }
    render() {
        const Button = (props) => {
            return (
                <button onClick={this.handleClick}>Click to increase age{this.state.clickCount}</button>
            );
        }
        return (<div>
            <h1>{this.props.last_name}, {this.props.first_name}</h1>
            <h4>Age: {this.props.age}</h4>
            <h4>Hair color: {this.props.hair_color}</h4>
            <Button handleClick={this.handleClick}/>
        </div>)
        // <Header first_name="Meg" last_name="Nielsen" age={1} hair_color="hairColor"/>);
    }
}

