import React, { Component } from 'react';

class Person extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clickCount: 0
        }
    }
    handleClick = () => {
        this.setState({
            clickCount: this.state.clickCount + 1
        })
    }
    render() {
        return (<div>
            <h1>{props.last_name}, {props.first_name}</h1>
            <h4>Age: {props.age}</h4>
            <h4>Hair color: {props.hair_color}</h4>
            <Button handleClick={this.handleClick}/>
        </div>)
        // <Header first_name="Meg" last_name="Nielsen" age={1} hair_color="hairColor"/>);
    }
}

const Button = (props) => {
    return (
        <button onClick={() => props.count+=1}>Click to increase age{props.count}</button>
    );
}

